import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import {
  HomeIcon,
  CalendarIcon,
  UserGroupIcon,
  Cog6ToothIcon,
  ChartBarIcon,
  ArrowRightOnRectangleIcon,
} from '@heroicons/react/24/outline';

const Navbar = () => {
  const [menuOpen, setMenuOpen] = useState(false);
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('user');
    setUser(null);
    setMenuOpen(false);
    navigate('/login');
  };

  return (
    <nav className="sticky top-0 z-50 bg-gradient-to-r from-orange-500 to-pink-500 text-white shadow-lg">
      <div className="max-w-7xl mx-auto flex items-center justify-between px-4 py-3">
        <h1 className="text-3xl font-bold tracking-wide drop-shadow-sm">📆 Booking System</h1>

        <button
          className="md:hidden focus:outline-none"
          onClick={() => setMenuOpen(!menuOpen)}
        >
          <svg className="w-8 h-8" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            {menuOpen ? (
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
            ) : (
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
            )}
          </svg>
        </button>

        <div className="hidden md:flex items-center space-x-6 font-medium">
          <NavLink to="/" label="Home" Icon={HomeIcon} />
          <NavLink to="/booking" label="Book" Icon={CalendarIcon} />
          <NavLink to="/dashboard" label="Dashboard" Icon={ChartBarIcon} />
          <NavLink to="/admin" label="Admin" Icon={Cog6ToothIcon} />
          <NavLink to="/businessDashboard" label="Business Dash" Icon={UserGroupIcon} />
        </div>

        <div className="hidden md:flex items-center space-x-4">
          {user ? (
            <>
              <span className="text-sm">Hi, {user.name || 'User'}</span>
              <button
                onClick={handleLogout}
                className="bg-red-500 hover:bg-red-600 text-white px-4 py-1 rounded shadow transition"
              >
                Logout
              </button>
            </>
          ) : (
            <>
              <Link
                to="/login"
                className="bg-white text-orange-700 px-4 py-1 rounded hover:bg-gray-100 transition"
              >
                Login
              </Link>
              <Link
                to="/signup"
                className="bg-pink-600 text-white px-4 py-1 rounded hover:bg-pink-700 transition"
              >
                Sign Up
              </Link>
            </>
          )}
        </div>
      </div>

      {/* Mobile Dropdown */}
      {menuOpen && (
        <div className="md:hidden bg-pink-600 transition-all duration-300 pb-4">
          <div className="flex flex-col items-center space-y-3 text-white text-lg pt-4">
            <MobileNavLink to="/" label="Home" />
            <MobileNavLink to="/booking" label="Book" />
            <MobileNavLink to="/dashboard" label="Dashboard" />
            <MobileNavLink to="/admin" label="Admin" />
            <MobileNavLink to="/businessDashboard" label="Business Dash" />
          </div>
          <div className="mt-4 text-center">
            {user ? (
              <>
                <span className="text-sm block mb-2">Hi, {user.name || 'User'}</span>
                <button
                  onClick={handleLogout}
                  className="bg-red-500 hover:bg-red-600 text-white px-4 py-1 rounded shadow transition"
                >
                  Logout
                </button>
              </>
            ) : (
              <>
                <Link
                  to="/login"
                  onClick={() => setMenuOpen(false)}
                  className="block bg-white text-orange-700 px-4 py-1 my-1 rounded hover:bg-gray-100 transition"
                >
                  Login
                </Link>
                <Link
                  to="/signup"
                  onClick={() => setMenuOpen(false)}
                  className="block bg-pink-600 text-white px-4 py-1 rounded hover:bg-pink-700 transition"
                >
                  Sign Up
                </Link>
              </>
            )}
          </div>
        </div>
      )}
    </nav>
  );
};

// Desktop nav link with icon
const NavLink = ({ to, label, Icon }) => (
  <Link
    to={to}
    className="flex items-center space-x-1 hover:text-yellow-100 transition"
  >
    <Icon className="h-5 w-5" />
    <span>{label}</span>
  </Link>
);

// Mobile-only nav links
const MobileNavLink = ({ to, label }) => (
  <Link
    to={to}
    onClick={() => setMenuOpen(false)}
    className="hover:text-yellow-100 transition"
  >
    {label}
  </Link>
);

export default Navbar;
