import React from 'react';
import { useNavigate } from 'react-router-dom';

const features = [
  {
    title: 'Real-time Availability',
    icon: 'ðŸ“…',
    desc: 'Easily check and book available time slots without delays.',
  },
  {
    title: 'Email Reminders',
    icon: 'ðŸ””',
    desc: 'Stay informed with automatic confirmation and reminder emails.',
  },
  {
    title: 'Secure Booking',
    icon: 'ðŸ”',
    desc: 'Your data is safe and protected with modern security standards.',
  },
  {
    title: 'Flexible Services',
    icon: 'ðŸ› ï¸',
    desc: 'Book consultations, salon visits, coaching sessions and more.',
  },
];

const Home = () => {
  const navigate = useNavigate();

  const handleBookingClick = () => {
    navigate('/booking');
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-orange-50 via-pink-100 to-purple-100 text-gray-800">
      {/* Hero Section */}
      <div className="p-8 flex-grow">
        <div className="max-w-6xl mx-auto grid md:grid-cols-2 gap-10 items-center mb-20">
          <img
            src="/images/booking-hero.png"
            alt="Booking Illustration"
            className="w-full h-auto rounded-2xl shadow-2xl transform hover:scale-105 transition-transform duration-300"
          />
          <div className="animate-fadeIn">
            <h1 className="text-5xl font-extrabold mb-6 text-purple-700 drop-shadow-lg">
              Welcome to the Booking Hub âœ¨
            </h1>
            <p className="text-lg text-gray-700 mb-6 leading-relaxed">
              Book appointments effortlessly with real-time availability and instant confirmations. Fast. Easy. Reliable.
            </p>
            <button
              onClick={handleBookingClick}
              className="bg-gradient-to-r from-purple-600 to-pink-600 text-white px-8 py-3 rounded-full shadow-lg hover:from-purple-700 hover:to-pink-700 transition-all duration-300"
            >
              Book an Appointment
            </button>
          </div>
        </div>

        {/* Features Section */}
        <div className="w-full max-w-5xl mx-auto text-center">
          <h2 className="text-4xl font-bold mb-12 text-gray-800">Why Choose Us?</h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-8">
            {features.map((feature, index) => (
              <div
                key={index}
                className="bg-white p-6 rounded-2xl shadow-md hover:shadow-xl transition-transform transform hover:-translate-y-2 duration-300"
              >
                <div className="text-5xl mb-4">{feature.icon}</div>
                <h3 className="text-xl font-semibold text-purple-700 mb-2">{feature.title}</h3>
                <p className="text-gray-600 text-sm">{feature.desc}</p>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
